import React, { Component } from 'react';
import uuid from 'uuid/v1';
import Balance from './Balance/Balance';
import Controls from './Controls/Controls';
import TransactionHistory from './TransactionHistory/TransactionHistory';
import s from '../styles.module.css';

export default class Dashboard extends Component {
  state = {
    transactions: [],
    balance: 0,
    amount: 0,
  };

  onDeposit = () => {
    this.setState(prevState => ({
      balance: prevState.balance + +prevState.amount,
    }));
    this.addNumber('DEPOSIT');
  };

  onWithdraw = () => {
    this.setState(prevState => ({
      balance: prevState.balance - +prevState.amount,
    }));
    this.addNumber('WITHDRAW');
  };

  onChange = e => {
    this.setState({ amount: +e.target.value });

    // this.reset();
  };

  addNumber = type => {
    console.log(this.state.transactions);
    const { amount } = this.state;
    if (amount > 0) {
      const newTransaction = {
        id: uuid(),
        type,
        amount,
        date: new Date().toLocaleString('en-US', { hour12: false }),
      };

      this.setState(
        state => ({
          transactions: [...state.transactions, newTransaction],
        }),
        () => console.log(this.state.transactions),
      );
    }
  };

  //  depositBalance = (transactions) => this.transactions.reduce((acc, el) => {
  //     if (el.type === 'DEPOSIT') {
  //       return +(acc + el.amount);
  //     }
  //   }, 0);

  reset = () => {
    this.setState({ amount: '' });
  };

  render() {
    const { transactions } = this.state;
    const {balance} = this.balance;

    const deposit = transactions.reduce((acc, el) => {
      if (el.type === 'DEPOSIT') {
        return acc + el.amount;
      }
      return acc;
    }, 0);

    const withdraw = transactions.reduce((acc, el) => {
      if (el.type === 'WITHDRAW') {
        return acc + el.amount;
      }
      return acc;
    }, 0);

    // console.log(deposit);
    return (
      <div className={s.dashboard}>
        <Controls
          onChange={this.onChange}
          onDeposit={this.onDeposit}
          onWithdraw={this.onWithdraw}
        />
        <Balance deposit={deposit} withdraw={withdraw} balance/>

        <TransactionHistory transactions={transactions} />
      </div>
    );
  }
}
